import React, { useState, useEffect } from "react";
import { fetchData } from "../services/fetchDataService";
import { realizarAccion } from "../services/actions";
import "../styles/Dashboard.css";

const Dashboard = ({ user }) => {
  const [vista, setVista] = useState("home"); 
  const [fotocopiadoras, setFotocopiadoras] = useState([]);

  useEffect(() => {
    const obtenerDatos = async () => {
      if (vista === "reserva") setFotocopiadoras(await fetchData("fotocopiadoras"));
    };
    obtenerDatos();
  }, [vista]);

  return (
    <div className="dashboard-container">
      <header>
        <h1>TecnoQuito</h1>
        <nav>
          <button onClick={() => setVista("home")}>Inicio</button>
          <button onClick={() => setVista("reserva")}>Reserva</button>
          <button onClick={() => setVista("compra")}>Compra</button>
          <button onClick={() => setVista("alquiler")}>Alquiler</button>
          {user.rol === "admin" && <button onClick={() => setVista("admin")}>Admin</button>}
          <button className="logout">Cerrar Sesi√≥n</button>
        </nav>
      </header>

      <main>
        {vista === "home" && <h2>Bienvenido a TecnoQuito</h2>}

        {vista === "reserva" && (
          <div>
            <h2>Fotocopiadoras Disponibles</h2>
            <ul>
              {fotocopiadoras.map((f) => (
                <li key={f.idfotocopiadora}>
                  {f.modelo} - {f.marca} - {f.ubicacion}
                  <button onClick={() => realizarAccion("reservar", f.idfotocopiadora, user.id)}>Reservar</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
