import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../AuthContext";
import { useNavigate } from "react-router-dom";
import "../styles/GestionFotocopiadoras.css";

const GestionFotocopiadoras = () => {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();
    const [fotocopiadoras, setFotocopiadoras] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState("");

    // Estado para el formulario desplegable
    const [showForm, setShowForm] = useState(false);
    const [editMode, setEditMode] = useState(false);
    const [selectedFotocopiadora, setSelectedFotocopiadora] = useState(null);

    useEffect(() => {
        if (!user || user.rol !== "admin") {
            navigate("/login");
            return;
        }

        const fetchFotocopiadoras = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/fotocopiadoras", {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${user.token}`,
                    },
                });

                if (!response.ok) {
                    throw new Error("Error al obtener los datos");
                }

                const data = await response.json();
                setFotocopiadoras(data);
            } catch (error) {
                console.error("ðŸš¨ Error al obtener fotocopiadoras:", error);
                setError("No se pudieron cargar los datos.");
            } finally {
                setLoading(false);
            }
        };

        fetchFotocopiadoras();
    }, [user, navigate]);

    const handleLogout = () => {
        logout();
        navigate("/login");
    };

    // âœ… Mostrar formulario para agregar
    const handleAgregar = () => {
        setEditMode(false);
        setSelectedFotocopiadora({
            modelo: "",
            marca: "",
            estado: "disponible",
            precio: "",
            descripcion: "",
            ubicacion: "",
            fecha_adquisicion: "",
            contador_impresiones: "",
        });
        setShowForm(true);
    };

    // âœ… Mostrar formulario para editar
    const handleEditar = (fotocopiadora) => {
        setEditMode(true);
        setSelectedFotocopiadora(fotocopiadora);
        setShowForm(true);
    };

    // âœ… Eliminar fotocopiadora
    const handleEliminar = async (id) => {
        if (!window.confirm("Â¿EstÃ¡s seguro de que quieres eliminar esta fotocopiadora?")) return;

        try {
            const response = await fetch(`http://localhost:5000/api/fotocopiadoras/${id}`, {
                method: "DELETE",
                headers: {
                    Authorization: `Bearer ${user.token}`,
                },
            });

            if (!response.ok) {
                throw new Error("Error al eliminar la fotocopiadora");
            }

            setFotocopiadoras(fotocopiadoras.filter((f) => f.idfotocopiadora !== id));
            alert("Fotocopiadora eliminada con Ã©xito");
        } catch (error) {
            console.error("ðŸš¨ Error al eliminar fotocopiadora:", error);
            alert("No se pudo eliminar la fotocopiadora.");
        }
    };

    // âœ… Manejar cambios en los campos del formulario
    const handleChange = (e) => {
        setSelectedFotocopiadora({ ...selectedFotocopiadora, [e.target.name]: e.target.value });
    };

    // âœ… Guardar cambios o agregar nueva fotocopiadora
    const handleGuardar = async () => {
        const url = editMode
            ? `http://localhost:5000/api/fotocopiadoras/${selectedFotocopiadora.idfotocopiadora}`
            : "http://localhost:5000/api/fotocopiadoras";

        const method = editMode ? "PUT" : "POST";

        try {
            const response = await fetch(url, {
                method,
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${user.token}`,
                },
                body: JSON.stringify(selectedFotocopiadora),
            });

            if (!response.ok) {
                throw new Error(`Error al ${editMode ? "modificar" : "agregar"} la fotocopiadora`);
            }

            const data = await response.json();
            alert(data.message);

            setFotocopiadoras((prev) =>
                editMode
                    ? prev.map((f) => (f.idfotocopiadora === selectedFotocopiadora.idfotocopiadora ? data.data : f))
                    : [...prev, data.data]
            );

            setShowForm(false);
        } catch (error) {
            console.error(`ðŸš¨ Error al ${editMode ? "modificar" : "agregar"} fotocopiadora:`, error);
            alert(`No se pudo ${editMode ? "modificar" : "agregar"} la fotocopiadora.`);
        }
    };

    return (
        <div className="admin-panel">
            <header className="admin-header">
                <div className="logo">
                    <div className="circle"></div>
                    <h1>TecnoQuito Admin</h1>
                </div>
                <nav className="menu">
                    <ul>
                        <li><a href="/admin">Dashboard</a></li>
                        <li><a href="/admin/fotocopiadoras">GestiÃ³n Fotocopiadoras</a></li>
                    </ul>
                </nav>
                <button className="logout-btn" onClick={handleLogout}>Cerrar SesiÃ³n</button>
            </header>

            <main className="admin-main">
                <h2>GestiÃ³n de Fotocopiadoras</h2>

                <div className="actions">
                    <input
                        type="text"
                        placeholder="Buscar fotocopiadora..."
                        className="search-bar"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <button className="add-btn" onClick={handleAgregar}>âž• Agregar</button>
                </div>

                {showForm && (
                    <div className="form-container">
                        <h3>{editMode ? "Editar" : "Agregar"} Fotocopiadora</h3>
                        <input type="text" name="modelo" placeholder="Modelo" value={selectedFotocopiadora.modelo} onChange={handleChange} />
                        <input type="text" name="marca" placeholder="Marca" value={selectedFotocopiadora.marca} onChange={handleChange} />
                        <input type="text" name="ubicacion" placeholder="UbicaciÃ³n" value={selectedFotocopiadora.ubicacion} onChange={handleChange} />
                        <input type="number" name="precio" placeholder="Precio" value={selectedFotocopiadora.precio} onChange={handleChange} />
                        <select name="estado" value={selectedFotocopiadora.estado} onChange={handleChange}>
                            <option value="disponible">Disponible</option>
                            <option value="alquilada">Alquilada</option>
                            <option value="vendida">Vendida</option>
                        </select>
                        <textarea name="descripcion" placeholder="DescripciÃ³n" value={selectedFotocopiadora.descripcion} onChange={handleChange}></textarea>
                        <input type="date" name="fecha_adquisicion" value={selectedFotocopiadora.fecha_adquisicion} onChange={handleChange} />
                        <input type="number" name="contador_impresiones" placeholder="Contador de impresiones" value={selectedFotocopiadora.contador_impresiones} onChange={handleChange} />
                        <button className="save-btn" onClick={handleGuardar}>{editMode ? "Modificar" : "Agregar"}</button>
                        <button className="cancel-btn" onClick={() => setShowForm(false)}>Cancelar</button>
                    </div>
                )}
            </main>
        </div>
    );
};

export default GestionFotocopiadoras;
