import React, { useState, useContext } from "react";
import { AuthContext } from "../AuthContext";
import { useNavigate } from "react-router-dom";
import "../styles/Reportes.css";

const ReportesAdmin = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [fechaInicio, setFechaInicio] = useState("");
  const [fechaFin, setFechaFin] = useState("");
  const [reporteData, setReporteData] = useState(null);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const generarReporte = async () => {
    if (!fechaInicio || !fechaFin) {
      alert("‚ö†Ô∏è Debes seleccionar una fecha de inicio y una fecha de fin.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:5000/api/reportes?fechaInicio=${fechaInicio}&fechaFin=${fechaFin}`
      );

      if (!response.ok) {
        throw new Error("Error al generar el reporte");
      }

      const data = await response.json();
      setReporteData(data);
    } catch (error) {
      console.error("üö® Error al generar el reporte:", error);
      alert("No se pudo generar el reporte.");
    }
  };

  return (
    <div className="admin-panel">
      {/* üîπ Encabezado de Administraci√≥n */}
      <header className="admin-header">
        <h1>TecnoQuito Admin</h1>
        <button className="logout-btn" onClick={handleLogout}>
          Cerrar Sesi√≥n
        </button>
      </header>

      {/* üîπ Contenido principal */}
      <main className="admin-main">
        <h2>Generar Reportes</h2>

        <div className="reportes-filtros">
          <label>Fecha Inicio:</label>
          <input
            type="date"
            value={fechaInicio}
            onChange={(e) => setFechaInicio(e.target.value)}
          />

          <label>Fecha Fin:</label>
          <input
            type="date"
            value={fechaFin}
            onChange={(e) => setFechaFin(e.target.value)}
          />

          <button className="report-btn" onClick={generarReporte}>
            üìä Generar Reporte
          </button>
        </div>

        {/* üîπ Mostrar datos del reporte si existen */}
        {reporteData && (
          <div className="reporte-resultados">
            <h3>Resultados del Reporte</h3>
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Modelo</th>
                  <th>Marca</th>
                  <th>Fecha Uso</th>
                  <th>Cliente</th>
                </tr>
              </thead>
              <tbody>
                {reporteData.map((item) => (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.modelo}</td>
                    <td>{item.marca}</td>
                    <td>{item.fecha_uso}</td>
                    <td>{item.cliente}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </main>
    </div>
  );
};

export default ReportesAdmin;
