import React, { useState, useContext } from "react";
import { AuthContext } from "../AuthContext"; // Importamos el contexto
import "../styles/FotocopiadorasTable.css";

const FotocopiadorasTable = ({ fotocopiadoras, tipo }) => {
  const { user } = useContext(AuthContext); // Obtener el usuario autenticado
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDates, setSelectedDates] = useState({});

  // Manejar cambios en la fecha
  const handleDateChange = (id, field, value) => {
    setSelectedDates((prevDates) => ({
      ...prevDates,
      [id]: { ...prevDates[id], [field]: value },
    }));
  };

  // Filtrar fotocopiadoras seg√∫n el t√©rmino de b√∫squeda
  const filteredFotocopiadoras = fotocopiadoras.filter((fotocopiadora) =>
    `${fotocopiadora.modelo} ${fotocopiadora.marca} ${fotocopiadora.ubicacion}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const handleAccion = (idfotocopiadora) => {
    if (!user || !user.id) {
      alert("Error: No se ha identificado el usuario.");
      return;
    }

    const { fechainicio, fechafin } = selectedDates[idfotocopiadora] || {};

    // Validar fechas si NO es una compra
    if (tipo !== "compra" && (!fechainicio || !fechafin)) {
      alert("Por favor selecciona las fechas antes de continuar.");
      return;
    }

    let endpoint = "";
    let mensaje = "";
    let bodyData = {
      idcliente: user.id,
      idfotocopiadora,
      fechainicio: fechainicio || null, // Asegurar que siempre tenga valor
    };

    if (tipo === "reserva") {
      endpoint = "http://localhost:5000/api/operaciones/reservar";
      mensaje = "Reservada con √©xito";
      bodyData.fechafin = fechafin || null;
    } else if (tipo === "compra") {
      endpoint = "http://localhost:5000/api/operaciones/comprar";
      mensaje =
        "Gracias por comprar con nosotros. Nos contactaremos con usted para finalizar el proceso.";
    } else if (tipo === "alquiler") {
      endpoint = "http://localhost:5000/api/operaciones/alquilar";
      mensaje =
        "Gracias por alquilar con nosotros. Nos contactaremos con usted para finalizar el proceso.";
      bodyData.fechafin = fechafin || null;
    }

    console.log("Datos enviados al backend:", JSON.stringify(bodyData)); // üõ†Ô∏è Debug para ver qu√© se env√≠a

    fetch(endpoint, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(bodyData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (tipo === "compra") {
          alert(`${mensaje}\nPrecio total: $${data.precio || "No disponible"}`);
        } else if (tipo === "alquiler") {
          alert(
            `${mensaje}\nPrecio total del alquiler: $${
              data.precio_total || "No disponible"
            }`
          );
        } else {
          alert(
            `${mensaje}\nPrecio aproximado: $${
              data.precio_aproximado || "No disponible"
            }`
          );
        }
      })
      .catch((error) => console.error("Error:", error));
  };

  return (
    <div className="table-container">
      <div className="search-container">
        <input
          type="text"
          placeholder="Buscar fotocopiadora..."
          className="search-bar"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <table>
        <thead>
          <tr>
            <th>Modelo</th>
            <th>Marca</th>
            <th>Ubicaci√≥n</th>
            <th>Fecha Inicio</th>
            {tipo !== "compra" && <th>Fecha Fin</th>}
            <th>Acci√≥n</th>
          </tr>
        </thead>
        <tbody>
          {filteredFotocopiadoras.map((fotocopiadora) => (
            <tr key={fotocopiadora.idfotocopiadora}>
              <td>{fotocopiadora.modelo}</td>
              <td>{fotocopiadora.marca}</td>
              <td>{fotocopiadora.ubicacion}</td>
              <td>
                <input
                  type="date"
                  value={
                    selectedDates[fotocopiadora.idfotocopiadora]?.fechainicio ||
                    ""
                  }
                  onChange={(e) =>
                    handleDateChange(
                      fotocopiadora.idfotocopiadora,
                      "fechainicio",
                      e.target.value
                    )
                  }
                />
              </td>
              {tipo !== "compra" && (
                <td>
                  <input
                    type="date"
                    value={
                      selectedDates[fotocopiadora.idfotocopiadora]?.fechafin ||
                      ""
                    }
                    onChange={(e) =>
                      handleDateChange(
                        fotocopiadora.idfotocopiadora,
                        "fechafin",
                        e.target.value
                      )
                    }
                  />
                </td>
              )}
              <td>
                <button
                  onClick={() => handleAccion(fotocopiadora.idfotocopiadora)}
                  disabled={
                    tipo !== "compra" &&
                    (!selectedDates[fotocopiadora.idfotocopiadora]?.fechainicio ||
                      !selectedDates[fotocopiadora.idfotocopiadora]?.fechafin)
                  }
                >
                  {tipo === "reserva"
                    ? "Reservar"
                    : tipo === "compra"
                    ? "Comprar"
                    : "Alquilar"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FotocopiadorasTable;
