import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../AuthContext";
import { useNavigate } from "react-router-dom";
import "../styles/GestionFotocopiadoras.css";

const GestionFotocopiadoras = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [fotocopiadoras, setFotocopiadoras] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    if (!user || user.rol !== "admin") {
      navigate("/login");
      return;
    }

    const fetchFotocopiadoras = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/fotocopiadoras", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Error al obtener los datos");
        }

        const data = await response.json();
        setFotocopiadoras(data);
      } catch (error) {
        console.error("üö® Error al obtener fotocopiadoras:", error);
        setError("No se pudieron cargar los datos.");
      } finally {
        setLoading(false);
      }
    };

    fetchFotocopiadoras();
  }, [user, navigate]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const handleAgregar = () => {
    navigate("/admin/fotocopiadoras/agregar");
  };

  const handleModificar = (id) => {
    navigate(`/admin/fotocopiadoras/modificar/${id}`);
  };

  const handleEliminar = async (id) => {
    const confirmacion = window.confirm("¬øEst√°s seguro de que deseas eliminar esta fotocopiadora?");
    if (!confirmacion) return;

    try {
      const response = await fetch(`http://localhost:5000/api/fotocopiadoras/${id}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${user.token}`
        }
      });

      if (!response.ok) {
        throw new Error("Error al eliminar la fotocopiadora");
      }

      alert("Fotocopiadora eliminada con √©xito.");
      setFotocopiadoras(fotocopiadoras.filter(f => f.idfotocopiadora !== id));
    } catch (error) {
      console.error("üö® Error al eliminar fotocopiadora:", error);
      alert("No se pudo eliminar la fotocopiadora.");
    }
  };

  const sortedFotocopiadoras = [...fotocopiadoras].sort((a, b) => a.idfotocopiadora - b.idfotocopiadora);

  const filteredFotocopiadoras = sortedFotocopiadoras.filter((fotocopiadora) =>
    `${fotocopiadora.modelo} ${fotocopiadora.marca} ${fotocopiadora.ubicacion} ${fotocopiadora.estado}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  return (
    <div className="admin-panel">
      <header className="admin-header">
        <div className="logo">
          <div className="circle"></div>
          <h1>TecnoQuito Admin</h1>
        </div>
        <nav className="menu">
          <ul>
            <li><a href="/admin">Dashboard</a></li>
            <li><a href="/admin/fotocopiadoras">Gesti√≥n Fotocopiadoras</a></li>
          </ul>
        </nav>
        <button className="logout-btn" onClick={handleLogout}>
          Cerrar Sesi√≥n
        </button>
      </header>

      <main className="admin-main">
        <h2>Gesti√≥n de Fotocopiadoras</h2>

        <div className="actions">
          <input
            type="text"
            placeholder="Buscar fotocopiadora..."
            className="search-bar"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="add-btn" onClick={handleAgregar}>
            ‚ûï Agregar
          </button>
        </div>

        {loading ? (
          <p>üîÑ Cargando fotocopiadoras...</p>
        ) : error ? (
          <p>‚ùå {error}</p>
        ) : filteredFotocopiadoras.length === 0 ? (
          <p>‚ö†Ô∏è No se encontraron fotocopiadoras con el criterio de b√∫squeda.</p>
        ) : (
          <table className="fotocopiadoras-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Modelo</th>
                <th>Marca</th>
                <th>Ubicaci√≥n</th>
                <th>Estado</th>
                <th>Acciones</th> {/* Nueva columna para acciones */}
              </tr>
            </thead>
            <tbody>
              {filteredFotocopiadoras.map((fotocopiadora) => (
                <tr key={fotocopiadora.idfotocopiadora}>
                  <td>{fotocopiadora.idfotocopiadora}</td>
                  <td>{fotocopiadora.modelo}</td>
                  <td>{fotocopiadora.marca}</td>
                  <td>{fotocopiadora.ubicacion}</td>
                  <td>{fotocopiadora.estado}</td>
                  <td>
                    <button className="edit-btn" onClick={() => handleModificar(fotocopiadora.idfotocopiadora)}>‚úèÔ∏è Modificar</button>
                    <button className="delete-btn" onClick={() => handleEliminar(fotocopiadora.idfotocopiadora)}>üóëÔ∏è Eliminar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </main>
    </div>
  );
};

export default GestionFotocopiadoras;
