import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const id = localStorage.getItem('id'); // ðŸ†• Obtener ID del usuario
    const rol = localStorage.getItem('rol');
    const correo = localStorage.getItem('correo');
    const nombre = localStorage.getItem('nombre');

    if (token && id && rol && correo && nombre) {
      setUser({ token, id, rol, correo, nombre }); // ðŸ†• Guardar ID en el contexto
    }
  }, []);

  const login = ({ token, id, rol, correo, nombre }) => { // ðŸ†• Incluir ID
    localStorage.setItem('token', token);
    localStorage.setItem('id', id); // ðŸ†• Guardar ID en localStorage
    localStorage.setItem('rol', rol);
    localStorage.setItem('correo', correo);
    localStorage.setItem('nombre', nombre);
    setUser({ token, id, rol, correo, nombre });
  };

  const logout = () => {
    localStorage.clear();
    setUser(null);
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
