import React, { useState, useEffect } from "react";

const ReportesReto = () => {
  const [reportes, setReportes] = useState([]);
  const [fotocopiadoraMasUsada, setFotocopiadoraMasUsada] = useState(null);
  const [fechaInicio, setFechaInicio] = useState("");
  const [fechaFin, setFechaFin] = useState("");

  const fetchReportes = async () => {
    if (!fechaInicio || !fechaFin) return;

    try {
      const response = await fetch(
        `http://3.144.47.31:5000/api/reportesReto?fechaInicio=${fechaInicio}&fechaFin=${fechaFin}`
      );
      const data = await response.json();
      setReportes(data.reportes);
      setFotocopiadoraMasUsada(data.fotocopiadora_mas_usada);
    } catch (error) {
      console.error("üö® Error al obtener reportes:", error);
    }
  };

  return (
    <div>
      <h2>üìä Reportes Reto</h2>
      
      <label>Fecha Inicio: </label>
      <input type="date" value={fechaInicio} onChange={(e) => setFechaInicio(e.target.value)} />

      <label>Fecha Fin: </label>
      <input type="date" value={fechaFin} onChange={(e) => setFechaFin(e.target.value)} />

      <button onClick={fetchReportes}>Generar Reporte</button>

      <h3>Resultados:</h3>
      <ul>
        {reportes.map((reporte, index) => (
          <li key={index}>
            {reporte.cliente} us√≥ {reporte.fotocopiadora} {reporte.veces_usada} veces.
          </li>
        ))}
      </ul>

      {fotocopiadoraMasUsada && (
        <div>
          <h3>üèÜ Fotocopiadora m√°s usada</h3>
          <p>
            {fotocopiadoraMasUsada.fotocopiadora_mas_usada} se us√≥ {fotocopiadoraMasUsada.cantidad_usada} veces.
          </p>
        </div>
      )}
    </div>
  );
};

export default ReportesReto;
