import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/AdminPanel.css';

const AdminPanel = () => {
  const [fotocopiadoras, setFotocopiadoras] = useState([]);
  const [nuevaFotocopiadora, setNuevaFotocopiadora] = useState({
    modelo: '',
    marca: '',
    estado: '',
    ubicacion: '',
  });

  useEffect(() => {
    const fetchFotocopiadoras = async () => {
      const response = await axios.get('http://localhost:5000/api/admin/fotocopiadoras');
      setFotocopiadoras(response.data);
    };
    fetchFotocopiadoras();
  }, []);

  const handleInputChange = (e) => {
    setNuevaFotocopiadora({ ...nuevaFotocopiadora, [e.target.name]: e.target.value });
  };

  const agregarFotocopiadora = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/admin/fotocopiadoras', nuevaFotocopiadora);
      setFotocopiadoras([...fotocopiadoras, response.data]);
      setNuevaFotocopiadora({ modelo: '', marca: '', estado: '', ubicacion: '' });
    } catch (error) {
      console.error('Error al agregar fotocopiadora:', error);
    }
  };

  const eliminarFotocopiadora = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/fotocopiadoras/${id}`);
      setFotocopiadoras(fotocopiadoras.filter((f) => f.idfotocopiadora !== id));
    } catch (error) {
      console.error('Error al eliminar fotocopiadora:', error);
    }
  };

  return (
    <div className="admin-panel">
      <h1>Panel de Administración</h1>
      <form onSubmit={agregarFotocopiadora}>
        <input
          type="text"
          name="modelo"
          placeholder="Modelo"
          value={nuevaFotocopiadora.modelo}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="marca"
          placeholder="Marca"
          value={nuevaFotocopiadora.marca}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="estado"
          placeholder="Estado"
          value={nuevaFotocopiadora.estado}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="ubicacion"
          placeholder="Ubicación"
          value={nuevaFotocopiadora.ubicacion}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Agregar</button>
      </form>
      <ul>
        {fotocopiadoras.map((f) => (
          <li key={f.idfotocopiadora}>
            {f.modelo} - {f.marca} - {f.estado} - {f.ubicacion}
            <button onClick={() => eliminarFotocopiadora(f.idfotocopiadora)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
