import React, { useState, useEffect } from "react";
import { Bar, Line, Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, LineElement, PointElement } from "chart.js";
import "../styles/DashboardAdmin.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, LineElement, PointElement);

const DashboardAdmin = () => {
  const [fechasMayorUso, setFechasMayorUso] = useState([]);
  const [horasMayorUso, setHorasMayorUso] = useState([]);
  const [fotocopiadorasMasUsadas, setFotocopiadorasMasUsadas] = useState([]);
  const [mejoresClientes, setMejoresClientes] = useState([]);
  const [fotocopiadorasMantenimiento, setFotocopiadorasMantenimiento] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const resFechas = await fetch("http://localhost:5000/api/dashboard/fechas-mayor-uso");
        const resHoras = await fetch("http://localhost:5000/api/dashboard/horas-mayor-uso");
        const resFotocopiadoras = await fetch("http://localhost:5000/api/dashboard/fotocopiadoras-mas-usadas");
        const resClientes = await fetch("http://localhost:5000/api/dashboard/mejores-clientes");
        const resMantenimiento = await fetch("http://localhost:5000/api/dashboard/fotocopiadoras-mantenimiento");

        const dataFechas = await resFechas.json();
        const dataHoras = await resHoras.json();
        const dataFotocopiadoras = await resFotocopiadoras.json();
        const dataClientes = await resClientes.json();
        const dataMantenimiento = await resMantenimiento.json();

        setFechasMayorUso(dataFechas);
        setHorasMayorUso(dataHoras);
        setFotocopiadorasMasUsadas(dataFotocopiadoras);
        setMejoresClientes(dataClientes);
        setFotocopiadorasMantenimiento(dataMantenimiento);
      } catch (error) {
        console.error("Error al obtener datos del dashboard:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="dashboard-container">
      <h1>📊 Dashboard de Administración</h1>

      <div className="charts-container">
        {/* Fechas de mayor uso */}
        <div className="chart">
          <h3>📅 Fechas con Mayor Uso</h3>
          <Bar
            data={{
              labels: fechasMayorUso.map((item) => item.fecha),
              datasets: [
                {
                  label: "Total Usos",
                  data: fechasMayorUso.map((item) => item.total_usos),
                  backgroundColor: "rgba(54, 162, 235, 0.6)",
                },
              ],
            }}
          />
        </div>

        {/* Horas de mayor uso */}
        <div className="chart">
          <h3>⏰ Horas con Mayor Uso</h3>
          <Line
            data={{
              labels: horasMayorUso.map((item) => `${item.hora}:00`),
              datasets: [
                {
                  label: "Total Usos",
                  data: horasMayorUso.map((item) => item.total_usos),
                  borderColor: "rgba(255, 99, 132, 1)",
                  backgroundColor: "rgba(255, 99, 132, 0.2)",
                  fill: true,
                },
              ],
            }}
          />
        </div>

        {/* Fotocopiadoras más usadas */}
        <div className="chart">
          <h3>🖨️ Fotocopiadoras Más Usadas</h3>
          <Doughnut
            data={{
              labels: fotocopiadorasMasUsadas.map((item) => `${item.modelo} - ${item.marca}`),
              datasets: [
                {
                  data: fotocopiadorasMasUsadas.map((item) => item.total_usos),
                  backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4CAF50", "#8E44AD"],
                },
              ],
            }}
          />
        </div>
      </div>

      <div className="charts-container">
        {/* Mejores clientes */}
        <div className="chart">
          <h3>🏆 Mejores Clientes</h3>
          <Bar
            data={{
              labels: mejoresClientes.map((item) => item.nombre),
              datasets: [
                {
                  label: "Total Reservas",
                  data: mejoresClientes.map((item) => item.total_reservas),
                  backgroundColor: "rgba(153, 102, 255, 0.6)",
                },
                {
                  label: "Total Alquileres",
                  data: mejoresClientes.map((item) => item.total_alquileres),
                  backgroundColor: "rgba(255, 159, 64, 0.6)",
                },
              ],
            }}
          />
        </div>

        {/* Fotocopiadoras en mantenimiento */}
        <div className="chart">
          <h3>⚠️ Fotocopiadoras en Mantenimiento</h3>
          <ul>
            {fotocopiadorasMantenimiento.map((item) => (
              <li key={item.idfotocopiadora}>
                {item.modelo} ({item.marca}) - Contador: {item.contador_impresiones} impresiones, Toner: {item.toner_actual}%
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default DashboardAdmin;
