import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../AuthContext';
import axios from 'axios';
import '../styles/Dashboard.css';

const Dashboard = () => {
    const { logout } = useContext(AuthContext);
    const [fotocopiadoras, setFotocopiadoras] = useState([]); // Estado para almacenar las fotocopiadoras
    const [search, setSearch] = useState(''); // Estado del buscador

    // Funci贸n para obtener las fotocopiadoras disponibles
    const fetchFotocopiadoras = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/fotocopiadoras/disponibles');
            setFotocopiadoras(response.data);
        } catch (error) {
            console.error('Error obteniendo las fotocopiadoras:', error);
        }
    };

    // Cargar las fotocopiadoras al montar el componente
    useEffect(() => {
        fetchFotocopiadoras();
    }, []);

    const handleLogout = () => {
        logout();
        window.location.href = '/login';
    };

    return (
        <div className="dashboard">
            <header className="dashboard-header">
                <div className="logo">
                    <div className="circle"></div>
                    <h1>TecnoQuito</h1>
                </div>
                <nav className="menu">
                    <ul>
                        <li><a href="#" onClick={(e) => { e.preventDefault(); fetchFotocopiadoras(); }}>Reserva</a></li>
                        <li><a href="#">Compra</a></li>
                        <li><a href="#">Alquiler</a></li>
                    </ul>
                </nav>
                <button className="logout-btn" onClick={handleLogout}>Cerrar Sesi贸n</button>
            </header>

            <main className="dashboard-main">
                <h2>Bienvenido a TecnoQuito</h2>
                <p>Ofrecemos servicios de reserva, compra y alquiler de fotocopiadoras empresariales.</p>

                {/* Buscador */}
                <input
                    type="text"
                    placeholder="Buscar fotocopiadora..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />

                {/* Lista de fotocopiadoras disponibles */}
                <div className="fotocopiadoras-container">
                    {fotocopiadoras
                        .filter(foto => foto.modelo.toLowerCase().includes(search.toLowerCase()))
                        .map(foto => (
                            <div key={foto.idfotocopiadora} className="fotocopiadora-card">
                                <h3>{foto.modelo}</h3>
                                <p>Marca: {foto.marca}</p>
                                <p>Ubicaci贸n: {foto.ubicacion}</p>
                                <button>Reservar</button>
                            </div>
                        ))}
                </div>
            </main>
        </div>
    );
};

export default Dashboard;
