import React, { useContext } from 'react';
import { AuthContext } from '../AuthContext';
import '../styles/Dashboard.css'; // Asegúrate de crear este archivo CSS

const Dashboard = () => {
  const { user } = useContext(AuthContext); // Obtiene información del usuario del contexto

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Dashboard</h1>
        <p>Bienvenido, <strong>{user?.nombre || 'Usuario'}</strong></p>
      </header>
      <main className="dashboard-main">
        <section className="user-info">
          <h2>Tu información</h2>
          <p><strong>Correo:</strong> {user?.correo}</p>
          <p><strong>Rol:</strong> {user?.rol}</p>
        </section>
        <section className="user-actions">
          <h2>Acciones disponibles</h2>
          <ul>
            <li>Ver estadísticas</li>
            <li>Editar perfil</li>
            <li>Historial de reservas</li>
          </ul>
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
