import axios from "axios";

const API_URL = "http://localhost:5000/api"; // ðŸ”„ Ajusta si usas un servidor en EC2

export const fetchData = async (tipo) => {
  const endpoints = {
    fotocopiadoras: `${API_URL}/fotocopiadoras`,
    reservas: `${API_URL}/reservas`,
    adminDashboard: `${API_URL}/admin/dashboard`,
  };

  try {
    const response = await axios.get(endpoints[tipo]);
    return response.data;
  } catch (error) {
    console.error(`Error obteniendo ${tipo}:`, error);
    return [];
  }
};
