import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState(''); // Estado para almacenar el mensaje del backend

  useEffect(() => {
    // Realizar la solicitud al backend
    axios
      .get('http://localhost:5000/api/test')
      .then((response) => {
        setMessage(response.data.message); // Almacena el mensaje del backend
      })
      .catch((error) => {
        console.error('Error al conectar con el backend:', error);
        setMessage('Error al conectar con el backend');
      });
  }, []);

  return (
    <div>
      <h1>Conexi√≥n Backend-Frontend</h1>
      <p>{message}</p> {/* Mostrar el mensaje del backend */}
    </div>
  );
}

export default App;
